<?php# -----------------------------------------------------------------------------------------# Created by: Glowtouch# File description: vehiclefine Model# -----------------------------------------------------------------------------------------if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Constratecity_model extends CI_model{        public function __construct()    {        parent::__construct();    }        public function display_constratecity()    {        $query = $this->db->get('cons_rate_city');        return $query->result();    }    public function add_constratecity($data)    {        $this->db->insert('cons_rate_city',$data);    }        /**      # Function :	update_city      # Purpose  :	update construction       # Return   :	Array     */    public function update_city($data, $id)    {        $this->db->where('id', $id);        $this->db->update('cons_rate_city', $data);    }    }