<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');/** * File Name : Depreciation Model * * Description : This is used to handle depreciation data  * * Created By : Gauthami * * Created Date : 01/03/2017 * * Last Modified By : Gauthami * * Last Modified Date : 13/04/2017 * */class Depreciation_model extends CI_Model{    /**      # Function :	__construct      # Purpose  :	Class constructor      # params 	 :	None      # Return   :	None     */    public function __construct()    {        parent::__construct();    }    /**      # Function :	display_depreciation      # Purpose  :	display depreciation      # Return   :	Array     */    public function display_depreciation()    {        $this->db->select('*');        $this->db->from('depreciaton');        $this->db->order_by("b_age", "ASC");        $query = $this->db->get();        return $query->result();    }    /**      # Function :	check_depreciation      # Purpose  :	check depreciation      # Return   :	Array     */    public function check_depreciation($b_age)    {        $this->db->select('dep');        $this->db->from('depreciaton');        $this->db->where('b_age', $b_age);        $this->db->limit(1);        $query = $this->db->get();        return $query->num_rows();    }    /**      # Function :	add_depreciation      # Purpose  :	Insert depreciation      # Return   :	Array     */    public function add_depreciation($data)    {        $this->db->insert('depreciaton', $data);    }}